	MULXQ	M1+40(SB), T7, T8	\
	ADDQ	T7, T0			\
	MOVQ	T0, 8+C			\	// C1_final
	ADCQ	T8, T2			\
	MULXQ	M1+48(SB), T8, T7	\
	MOVQ	T8, 32+C		\
	ADCQ	T7, T4			\
	MULXQ	M1+56(SB), T8, T0	\
	MOVQ	T8, 40+C		\
	ADCQ	T1, T0			\
	MULXQ	M1+64(SB), T7, T1	\
	ADCQ	T5, T1			\
	MULXQ	M1+72(SB), T8, T5	\
	ADCQ	T5, T3			\
	MULXQ	M1+80(SB), T9, T5	\
	ADCQ	T5, T6			\
	MULXQ	M1+88(SB), DX, T5	\
	ADCQ	AX, T5			\
